// when action is dispatched to reducer,
// reducer is automatically passed previous state as 1st arg
// Everytime we update sth in our store, this reducer will be returning the updated state
const projectsReducer = (state = { projects: [], stacks: [], selectedStackIds: [], filteredProjects: [], comments: [], loading: false }, action) => {
  switch (action.type) {
    case 'LOADING_STACKS':
      return {
        ...state,
        stacks: [...state.stacks],
        loading: true
      }

    case 'ADD_STACKS':
      return {
        ...state,
        stacks: action.stacks,
        loading: false
      }

    case 'LOADING_PROJECTS':
      return {
        ...state,
        projects: [...state.projects],
        loading: true
      }

    case 'ADD_PROJECTS':
      return {
        ...state,
        projects: action.projects,
        filteredProjects: action.projects,
        loading: false
      }

    case 'ADD_FILTER':
      let filterProjects = state.filteredProjects.filter(proj => {
        return proj.stacks.some(stack => stack.id.toString() === action.stackId)
      })
      return {
        ...state,
        selectedStackIds: [...state.selectedStackIds, action.stackId],
        filteredProjects: filterProjects,
      }

    case 'REMOVE_FILTER':
      // let refilteredProjects = state.projects.filter(proj => {
      //   return proj.stacks.some(stack => stack.id.toString() === action.stackId)
      // })
      return {
        ...state,
        // selectedStackIds: state.selectedStackIds.filter(id => id !== action.stackId),
        // projects: refilteredProjects
      }

    default:
      return state;
  }
}


export default projectsReducer;
