  // }

  // handleOnSubmit = event => {
  //   event.preventDefault();
  //   this.props.filterProjects(this.state.stacks);
  //   this.setState({
  //     stacks: '',
  //   });
  // }

  render() {
    const { stacks, addFilter, removeFilter } = this.props
    return (
      <div>
        <p>Filter projects by stacks</p>
        <div className="btn-group-toggle" data-toggle="buttons" >
          {stacks.map(stack => <FilterButton stack={stack} addFilter={addFilter} removeFilter={removeFilter} />)}
        </div>

      </div>
    );
  }
}

export default ProjectFilters;
