import React, { Component } from 'react';
import Form from 'react-bootstrap/Form';
import Card from 'react-bootstrap/Card';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';

class CommentInput extends Component {
  state = {
    author: '',
    email: '',
    content: '',
  }
  // sets a property of projectId on the comment input
  // from the parent components id
  handleOnChange = event => {
    // Abstracting `setState` When `onChange` is triggered
    // name attributes matching keys in our state
    this.setState({
      [event.target.name]: event.target.value,
    });
  }

  handleOnSubmit = event => {
    event.preventDefault()
    const comment = {
      ...this.state,
      project_id: this.props.projectId,
    }
    // Send State to our addComment action creator
    console.log(comment)
    this.props.addComment(comment);
    // Update component state to return to default state
    this.setState({
      author: '',
      email: '',
      content: '',
    });
  }


  render() {
    return (
      // controlled form - state of component controlled by React
      <div className='comment-form'>

        {/* // <form onSubmit={this.handleOnSubmit}>
        //   <label>Name</label>
        //   <input
        //     type="text"
        //     value={this.state.name}
        //     onChange={this.handleOnChange} />
        //   <label>Email</label>
        //   <input
        //     type="text"
        //     value={this.state.email}
        //     onChange={this.handleOnChange} />
        //   <label>Comment here</label>
        //   <input
        //     type="text"
        //     value={this.state.content}
        //     onChange={this.handleOnChange} />
        //   <input type="submit" />
        // </form> */}
        <Card>
          <Form role="form" className='clearfix' onSubmit={this.handleOnSubmit}>
            <h5>Leave a comment</h5>
            <Form.Row>
              <Col>
                <Form.Group controlId="formAuthor">
                  {/* <Form.Label>Name</Form.Label> */}
                  <Form.Control
                    name="author"
                    type="text"
                    placeholder="Your name"
                    value={this.state.author}
                    required
                    onChange={this.handleOnChange}
                  />
                </Form.Group>
              </Col>
              <Col>
                <Form.Group controlId="formEmail">
                  {/* <Form.Label>Email address</Form.Label> */}
                  <Form.Control
                    name="email"
                    type="email"
                    placeholder="Your email"
                    value={this.state.email}
                    required
                    onChange={this.handleOnChange}
                  />
                  <Form.Text className="text-muted">
                    We'll never share your email with anyone else.
                  </Form.Text>
                </Form.Group>
              </Col>
            </Form.Row>
            <Form.Group controlId="formContent">
              {/* <Form.Label>Enter your comment</Form.Label> */}
              <Form.Control
                name="content"
                as="textarea"
                rows="3"
                placeholder="Enter your comment here..."
                value={this.state.content}
                required
                onChange={this.handleOnChange}
              />
            </Form.Group>
            <Button className="float-right" type="submit">
              Post
            </Button>
          </Form>
        </Card>

      </div>
    );
  }
};

export default CommentInput;
