import React, { Component } from 'react';
import Form from 'react-bootstrap/Form';
import Card from 'react-bootstrap/Card';
import { Row, Col } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';

class CommentInput extends Component {
  state = {
    name: '',
    email: '',
    date: '',
    content: '',
  }
  // sets a property of projectId on the comment input
  // from the parent components id
  handleOnChange = event => {
    this.setState({
      [event.target.name]: event.target.value,
    });
  }

  handleOnSubmit = event => {
    event.preventDefault();
    const comment = {
      ...this.state, projectId: this.props.projectId
    }
    this.props.addComment(comment);
    // Update component state to return to default state
    this.setState({
      name: '',
      email: '',
      date: '',
      content: '',
    });
  }


  render() {
    return (
      <div>

        {/* // <form onSubmit={this.handleOnSubmit}>
        //   <label>Name</label>
        //   <input
        //     type="text"
        //     value={this.state.name}
        //     onChange={this.handleOnChange} />
        //   <label>Email</label>
        //   <input
        //     type="text"
        //     value={this.state.email}
        //     onChange={this.handleOnChange} />
        //   <label>Comment here</label>
        //   <input
        //     type="text"
        //     value={this.state.content}
        //     onChange={this.handleOnChange} />
        //   <input type="submit" />
        // </form> */}
        <Card>
          <Form role="form" class='clearfix'>
            <h5>Leave a comment</h5>
            <Form.Row>
              <Col>
                <Form.Group controlId="formBasicText">
                  {/* <Form.Label>Name</Form.Label> */}
                  <Form.Control id='name' type="text" placeholder="Your name" />
                </Form.Group>
              </Col>
              <Col>
                <Form.Group controlId="formBasicEmail">
                  {/* <Form.Label>Email address</Form.Label> */}
                  <Form.Control id="email" type="email" placeholder="Your email" />
                  <Form.Text className="text-muted">
                    We'll never share your email with anyone else.
                  </Form.Text>
                </Form.Group>
              </Col>
            </Form.Row>
            <Form.Row>
              <Form.Group controlId="formBasicText" className="col-bg-12">
                {/* <Form.Label>Your comment</Form.Label> */}
                <Form.Control id="content" type="text" placeholder="Type your comment here..." />
              </Form.Group>
            </Form.Row>
            <Button variant="primary" type="submit">
              Post
          </Button>
          </Form>
        </Card>

      </div>
    );
  }
};

export default CommentInput;
