const blogPostsReducer = (state = {
  blogPosts: [],
  loading: false
}, action) => {
  switch (action.type) {
    case 'LOADING_BLOG':
      return {
        ...state,
        blogPosts: [...state.blogPosts],
        loading: true
      }

    case 'ADD_BLOG':
      return {
        ...state,
        blogPosts: action.blog,
        loading: false
      }

    default:
      return state
  }

}
export default blogPostsReducer;
